<div class="row">    <table class="table hi-roundedDashboardTagsTable">        <thead class="hi-roundedDashboardTagsTable_thead">        <tr class="hi-roundedDashboardTagsTable_thead_tr_td_m">            <td class="pt-4 hi-roundedDashboardTagsTable_thead_tr_td_r text-right"><h5>دسته بندی ها</h5></td>            <td class="hi-roundedDashboardTagsTable_thead_tr_td_m"></td>            <td class="pl-4 hi-roundedDashboardTagsTable_thead_tr_td_l">                {!! Form::open(['method'=>'POST', 'action'=>'CategoryController@multiDestroy', 'id'=>'deleteForm']) !!}                    <button id="multiDestroy" type="button" class="btn pull-left pt-2 pb-1 mt-1                                             hi-roundedDashboardTagsTable_thead_tr_td_button">حذف                    </button>                {!! Form::close() !!}            </td>        </tr>        </thead>        <tbody>            @foreach($categories as $category)                @component('components.CategoryRow')                    @slot('id') {{ $category->id }} @endslot                    @slot('category') {{ $category->category }} @endslot                    @slot('style2')@endslot                    @slot('style3')@endslot                @endcomponent            @endforeach        </tbody>    </table></div>{{ $categories->links() }}<script>    $('.singleDestroy').click(function (event) {        event.preventDefault();        var singleDeleteUrl = this.action;        var dataArray = $(this).serializeArray();        $.ajax({            type: "POST",            url: singleDeleteUrl,            data: dataArray        }).success(function (data) {            $('#loadCategories').html(data);            ShowNotification('دسته بندی پاک شد');        }).fail(function () {            ShowNotification('لطفا دوباره تلاش کنید');        });        window.history.pushState("", "", "http://dashboard.dev/categories");    });    var token = $('#deleteForm').find('input[name=_token]').val();    var csrf = {        name: '_token',        value: token    };    var checkboxes = [];    var selectedCategories = {};    $('input[type=checkbox]').click(function () {        var checkedId = this.id;        if(this.checked){            checkboxes.push(checkedId);        }else{            $.each(checkboxes, function (index, value) {                if(checkedId === value){                    checkboxes.splice(index, 1);                }            });        }        selectedCategories = {            name: 'ids',            value: checkboxes        };    });    $('#multiDestroy').click(function (event) {        event.preventDefault();        $.ajax({            type: "POST",            url: "{{ route('categories.multiDestroy') }}",            data: [                csrf, selectedCategories            ]        }).success(function (data) {            $('#loadCategories').html(data);            ShowNotification('دسته بندی (ها) پاک شدند');        }).fail(function () {            ShowNotification('یک یا چند دسته بندی انتخاب کنید');        });        window.history.pushState("", "", "http://dashboard.dev/categories");    });    /**     *      prepare form for edit start     */    $('.edit').click(function (event) {        event.preventDefault();        var editCategoryUrl = this.href;        var editForm = $('#editForm');        $.ajax({            type: "GET",            url: editCategoryUrl        }).success(function (data) {            editForm.attr('action', 'http://dashboard.dev/categories/' + JSON.parse(data).id);            $('#create-div').fadeOut();            $('#edit-div').fadeIn();            editForm.find('input[type=text]').val(JSON.parse(data).category);            ShowNotification('دسته بندی آماده ویرایش می باشد');        }).fail(function () {            ShowNotification('دوباره تلاش کنید');        });    });    /**     *      prepare form for edit end     */</script>